# Create .mcp.json file in home directory
cat > ~/.mcp.json << 'EOF'
{
  "mcpServers": {
    "context7": {
      "command": "npx",
      "args": ["-y", "@upstash/context7-mcp"]
    },
    "filesystem": {
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-filesystem", "/Users/forato"]
    }
  }
}
EOF

# Verify the file was created
echo "MCP servers configured:"
cat ~/.mcp.json

# Save setup state
mkdir -p .omc/state
cat > .omc/state/setup-state.json << 'EOF'
{
  "lastCompletedStep": 6,
  "timestamp": "$(date -u +"%Y-%m-%dT%H:%M:%SZ")"
}
EOF

