/*
Theme Name: RE Arquitetura & Design
Theme URI: https://re-arquitetura.com.br
Author: RE Arquitetura
Description: Tema minimalista para estúdio de arquitetura com Quiet Luxury aesthetic
Version: 1.0.0
Text Domain: re-arquitetura
Requires PHP: 8.2
*/

/* Reset e Base */
*, *::before, *::after {
	box-sizing: border-box;
}

html {
	scroll-behavior: smooth;
}

body {
	margin: 0;
	padding: 0;
	font-family: var(--font-sans, 'Montserrat', system-ui, sans-serif);
	font-size: var(--text-base, 1rem);
	line-height: 1.6;
	color: var(--color-text-primary, var(--color-greige-900));
	background-color: var(--color-bg-primary, var(--color-greige-50));
}

/* Tipografia */
h1, h2, h3, h4, h5, h6 {
	font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
	font-weight: var(--font-normal, 400);
	margin: 0;
	line-height: 1.2;
}

p {
	margin: 0 0 var(--space-4, 1rem);
}

/* Links */
a {
	color: inherit;
	text-decoration: none;
	transition: opacity 0.3s ease;
}

a:hover {
	opacity: 0.7;
}

/* Imagens */
img {
	max-width: 100%;
	height: auto;
	display: block;
}

/* Botões */
button {
	cursor: pointer;
	border: none;
	background: none;
	font-family: inherit;
}

/**
 * Hero Component Styles
 *
 * Performance-optimized CSS for Hero section
 * Uses CSS custom properties for theming and responsive design
 *
 * @package RE_Arquitetura
 */

/* ==========================================================================
   HERO SECTION
   ========================================================================== */

.hero {
    position: relative;
    height: 100vh;
    min-height: 600px;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
}

/* ==========================================================================
   HERO BACKGROUND & IMAGE
   ========================================================================== */

.hero__background {
    position: absolute;
    inset: 0;
    z-index: 0;
}

.hero__image {
    width: 100%;
    height: 100%;
    object-fit: cover;

    /* Prevenir CLS - Critical para LCP */
    aspect-ratio: 16/9;
}

/* ==========================================================================
   HERO OVERLAY
   ========================================================================== */

.hero__overlay {
    position: absolute;
    inset: 0;
    z-index: 0;
    background: linear-gradient(
        to bottom,
        rgba(20, 18, 15, 0.3) 0%,
        rgba(20, 18, 15, 0.5) 50%,
        rgba(20, 18, 15, 0.7) 100%
    );
    pointer-events: none;
}

/* ==========================================================================
   HERO CONTENT
   ========================================================================== */

.hero__content {
    position: relative;
    z-index: 1;
    text-align: center;
    color: var(--color-greige-50, #faf8f6);
    padding: var(--space-6, 1.5rem);
    max-width: 800px;
    margin: 0 auto;
}

.hero__title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-3xl, 2.441rem);
    font-weight: var(--font-normal, 400);
    line-height: var(--leading-tight, 1.1);
    letter-spacing: var(--tracking-tight, -0.02em);
    margin-bottom: var(--space-4, 1rem);
    color: inherit;
}

@media (min-width: 480px) {
    .hero__title {
        font-size: var(--text-4xl, 3.052rem);
    }
}

@media (min-width: 768px) {
    .hero__title {
        font-size: var(--text-5xl, 3.815rem);
    }
}

@media (min-width: 1280px) {
    .hero__title {
        font-size: var(--text-6xl, 4.769rem);
    }
}

.hero__subtitle {
    font-family: var(--font-sans, system-ui, -apple-system, sans-serif);
    font-size: var(--text-lg, 1.125rem);
    font-weight: var(--font-light, 300);
    letter-spacing: var(--tracking-wide, 0.025em);
    margin-bottom: var(--space-8, 2rem);
    color: inherit;
}

@media (min-width: 768px) {
    .hero__subtitle {
        font-size: var(--text-xl, 1.25rem);
    }
}

/* ==========================================================================
   HERO CTA BUTTON
   ========================================================================== */

.hero__cta {
    display: inline-block;
    padding: var(--space-3, 0.75rem) var(--space-6, 1.5rem);
    border: 1px solid var(--color-greige-50, #faf8f6);
    color: var(--color-greige-50, #faf8f6);
    font-family: var(--font-sans, system-ui, -apple-system, sans-serif);
    font-size: var(--text-sm, 0.875rem);
    font-weight: var(--font-normal, 400);
    letter-spacing: var(--tracking-wide, 0.025em);
    text-decoration: none;
    text-transform: uppercase;
    transition: all var(--transition-base, 150ms ease-in-out);
}

.hero__cta:hover,
.hero__cta:focus {
    background-color: var(--color-greige-50, #faf8f6);
    color: var(--color-greige-900, #14120f);
    outline: none;
}

.hero__cta:focus-visible {
    outline: 2px solid var(--color-greige-50, #faf8f6);
    outline-offset: 4px;
}

/* ==========================================================================
   HERO SCROLL INDICATOR
   ========================================================================== */

.hero__scroll {
    position: absolute;
    bottom: var(--space-8, 2rem);
    left: 50%;
    transform: translateX(-50%);
    z-index: 1;
    width: 1px;
    height: 60px;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-decoration: none;
    color: inherit;
}

.hero__scroll::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to bottom, var(--color-greige-50, #faf8f6), transparent);
    opacity: 0.5;
}

.hero__scroll span {
    width: 100%;
    height: 100%;
    display: block;
}

/* Scroll animation */
@keyframes heroScrollPulse {
    0%, 100% {
        opacity: 0.3;
        transform: translateY(0);
    }
    50% {
        opacity: 0.8;
        transform: translateY(10px);
    }
}

.hero__scroll::after {
    content: '';
    position: absolute;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 1px;
    height: 20px;
    background: var(--color-greige-50, #faf8f6);
    animation: heroScrollPulse 2s ease-in-out infinite;
}

/* ==========================================================================
   ACCESSIBILITY & REDUCED MOTION
   ========================================================================== */

@media (prefers-reduced-motion: reduce) {
    .hero__cta {
        transition: none;
    }

    .hero__scroll::after {
        animation: none;
    }
}

/* Critical Hero CSS - Inline for LCP Optimization
 * Minified version for wp_head inline
 * Extracted from hero.css for above-the-fold rendering
 */
.hero{position:relative;height:100vh;min-height:600px;display:flex;align-items:center;justify-content:center;overflow:hidden}.hero__background{position:absolute;inset:0;z-index:0}.hero__image{width:100%;height:100%;object-fit:cover;aspect-ratio:16/9}.hero__overlay{position:absolute;inset:0;z-index:0;background:linear-gradient(to bottom,rgba(20,18,15,.3)0,rgba(20,18,15,.5)50%,rgba(20,18,15,.7)100%);pointer-events:none}.hero__content{position:relative;z-index:1;text-align:center;color:#faf8f6;padding:1.5rem;max-width:800px;margin:0 auto}.hero__title{font-family:'Playfair Display',Georgia,serif;font-size:2.441rem;font-weight:400;line-height:1.1;letter-spacing:-.02em;margin-bottom:1rem;color:inherit}.hero__subtitle{font-family:system-ui,-apple-system,sans-serif;font-size:1.125rem;font-weight:300;letter-spacing:.025em;margin-bottom:2rem;color:inherit}.hero__cta{display:inline-block;padding:.75rem 1.5rem;border:1px solid #faf8f6;color:#faf8f6;font-family:system-ui,-apple-system,sans-serif;font-size:.875rem;font-weight:400;letter-spacing:.025em;text-decoration:none;text-transform:uppercase;transition:all 150ms ease-in-out}.hero__cta:hover,.hero__cta:focus{background-color:#faf8f6;color:#14120f}.hero__scroll{position:absolute;bottom:2rem;left:50%;transform:translateX(-50%);z-index:1;width:1px;height:60px;display:flex;flex-direction:column;align-items:center;text-decoration:none;color:inherit}@media (min-width:480px){.hero__title{font-size:3.052rem}}@media (min-width:768px){.hero__title{font-size:3.815rem}.hero__subtitle{font-size:1.25rem}}@media (min-width:1280px){.hero__title{font-size:4.769rem}}

/**
 * RE Arquitetura - Design Tokens
 * CSS Custom Properties for consistent design system
 */

:root {
  /* Color Tokens - Greige/Sand Palette */
  --color-greige-50: #faf8f6;
  --color-greige-100: #f5f0eb;
  --color-greige-200: #e8e0d8;
  --color-greige-300: #d4ccc4;
  --color-greige-400: #b8b0a8;
  --color-greige-500: #9c948c;
  --color-greige-600: #807870;
  --color-greige-700: #645c54;
  --color-greige-800: #484038;
  --color-greige-900: #2c241c;

  /* Sand Accent */
  --color-sand-100: #f0e6dc;
  --color-sand-200: #e0d0c0;
  --color-sand-300: #d0bba4;

  /* Matte Black */
  --color-black-matte: #1a1a1a;

  /* Semantic Colors */
  --color-text-primary: var(--color-greige-900);
  --color-text-secondary: var(--color-greige-600);
  --color-bg-primary: var(--color-greige-50);
  --color-bg-secondary: var(--color-greige-100);

  /* Typography Tokens */
  --font-serif: 'Playfair Display', Georgia, serif;
  --font-sans: 'Montserrat', system-ui, sans-serif;

  /* Modular Scale 1.250 */
  --text-xs: 0.64rem;
  --text-sm: 0.8rem;
  --text-base: 1rem;
  --text-lg: 1.25rem;
  --text-xl: 1.563rem;
  --text-2xl: 1.953rem;
  --text-3xl: 2.441rem;
  --text-4xl: 3.052rem;
  --text-5xl: 3.815rem;
  --text-6xl: 4.769rem;

  /* Font Weights */
  --font-light: 300;
  --font-normal: 400;
  --font-medium: 500;

  /* Line Heights */
  --leading-tight: 1.1;
  --leading-normal: 1.5;
  --leading-relaxed: 1.75;

  /* Letter Spacing */
  --tracking-tight: -0.02em;
  --tracking-normal: 0;
  --tracking-wide: 0.05em;

  /* Spacing Tokens (8px base unit) */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-5: 1.25rem;
  --space-6: 1.5rem;
  --space-8: 2rem;
  --space-10: 2.5rem;
  --space-12: 3rem;
  --space-16: 4rem;
  --space-20: 5rem;
  --space-24: 6rem;
  --space-32: 8rem;

  /* Breakpoint Tokens */
  --breakpoint-xs: 320px;
  --breakpoint-sm: 640px;
  --breakpoint-md: 768px;
  --breakpoint-lg: 1024px;
  --breakpoint-xl: 1280px;
  --breakpoint-2xl: 1536px;
  --breakpoint-3xl: 1920px;

  /* Container Widths */
  --container-sm: 640px;
  --container-md: 768px;
  --container-lg: 1024px;
  --container-xl: 1280px;
  --container-2xl: 1440px;

  /* Border Radius */
  --radius-sm: 0.125rem;
  --radius-md: 0.25rem;
  --radius-lg: 0.5rem;
  --radius-full: 9999px;

  /* Transitions */
  --transition-fast: 150ms ease-in-out;
  --transition-normal: 300ms ease-in-out;
  --transition-slow: 500ms ease-in-out;

  /* Shadows */
  --shadow-sm: 0 1px 2px 0 rgba(0, 0, 0, 0.05);
  --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);

  /* Z-Index Scale */
  --z-base: 0;
  --z-above: 10;
  --z-dropdown: 100;
  --z-sticky: 200;
  --z-fixed: 300;
  --z-modal: 400;
  --z-popover: 500;
  --z-tooltip: 600;
}

/**
 * RE Arquitetura - Base Styles
 * Modern CSS reset and base typography
 */

/* CSS Reset */
*, *::before, *::after {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

html {
  font-size: 16px;
  scroll-behavior: smooth;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body {
  font-family: var(--font-sans);
  font-size: var(--text-base);
  font-weight: var(--font-normal);
  line-height: var(--leading-normal);
  letter-spacing: var(--tracking-normal);
  color: var(--color-text-primary);
  background-color: var(--color-bg-primary);
  min-height: 100vh;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
  font-family: var(--font-serif);
  font-weight: var(--font-normal);
  line-height: var(--leading-tight);
  letter-spacing: var(--tracking-tight);
  color: var(--color-text-primary);
  margin-bottom: var(--space-4);
}

h1 { font-size: var(--text-5xl); }
h2 { font-size: var(--text-4xl); }
h3 { font-size: var(--text-3xl); }
h4 { font-size: var(--text-2xl); }
h5 { font-size: var(--text-xl); }
h6 { font-size: var(--text-lg); }

@media (min-width: 768px) {
  h1 { font-size: var(--text-6xl); }
  h2 { font-size: var(--text-5xl); }
  h3 { font-size: var(--text-4xl); }
  h4 { font-size: var(--text-3xl); }
  h5 { font-size: var(--text-2xl); }
  h6 { font-size: var(--text-xl); }
}

p {
  margin-bottom: var(--space-4);
  max-width: 65ch;
}

/* Links */
a {
  color: var(--color-text-primary);
  text-decoration: none;
  transition: color var(--transition-fast);
}

a:hover {
  color: var(--color-greige-600);
}

a:focus-visible {
  outline: 2px solid var(--color-greige-400);
  outline-offset: 2px;
}

/* Lists */
ul, ol {
  margin-left: var(--space-6);
  margin-bottom: var(--space-4);
}

li {
  margin-bottom: var(--space-2);
}

/* Images */
img, picture, video, canvas, svg {
  display: block;
  max-width: 100%;
  height: auto;
}

/* Forms */
input, button, textarea, select {
  font: inherit;
  color: inherit;
}

button {
  cursor: pointer;
  background: none;
  border: none;
}

/* Selection */
::selection {
  background-color: var(--color-greige-300);
  color: var(--color-text-primary);
}

/* Focus visible for accessibility */
:focus-visible {
  outline: 2px solid var(--color-greige-400);
  outline-offset: 2px;
}

/* Remove default focus outline for mouse users */
:focus:not(:focus-visible) {
  outline: none;
}

/* Heading with serif italic accent */
h1 em, h2 em, h3 em, h4 em, h5 em, h6 em {
  font-style: italic;
  font-weight: var(--font-normal);
}

/* Blockquotes */
blockquote {
  font-family: var(--font-serif);
  font-size: var(--text-xl);
  font-style: italic;
  line-height: var(--leading-relaxed);
  margin: var(--space-8) 0;
  padding-left: var(--space-6);
  border-left: 3px solid var(--color-greige-300);
}

@media (min-width: 768px) {
  blockquote {
    font-size: var(--text-2xl);
    padding-left: var(--space-8);
  }
}

/* Code */
code, pre {
  font-family: 'SF Mono', 'Monaco', 'Inconsolata', 'Fira Code', monospace;
  font-size: 0.9em;
}

code {
  background-color: var(--color-greige-100);
  padding: 0.125em 0.25em;
  border-radius: var(--radius-sm);
}

pre {
  background-color: var(--color-greige-100);
  padding: var(--space-4);
  border-radius: var(--radius-md);
  overflow-x: auto;
  margin-bottom: var(--space-6);
}

pre code {
  background: none;
  padding: 0;
}

/* Horizontal Rule */
hr {
  border: none;
  border-top: 1px solid var(--color-greige-200);
  margin: var(--space-8) 0;
}

/* Table */
table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: var(--space-6);
}

th, td {
  padding: var(--space-3) var(--space-4);
  text-align: left;
  border-bottom: 1px solid var(--color-greige-200);
}

th {
  font-family: var(--font-sans);
  font-weight: var(--font-medium);
  font-size: var(--text-sm);
  text-transform: uppercase;
  letter-spacing: var(--tracking-wide);
  color: var(--color-text-secondary);
}

/**
 * RE Arquitetura - Utility Classes
 * Reusable utility classes following BEM methodology
 */

/* Container Utilities */
.container {
  width: 100%;
  max-width: var(--container-2xl);
  margin-left: auto;
  margin-right: auto;
  padding-left: var(--space-4);
  padding-right: var(--space-4;
}

@media (min-width: 640px) {
  .container {
    padding-left: var(--space-6);
    padding-right: var(--space-6);
  }
}

@media (min-width: 1024px) {
  .container {
    padding-left: var(--space-8);
    padding-right: var(--space-8);
  }
}

.container--sm { max-width: var(--container-sm); }
.container--md { max-width: var(--container-md); }
.container--lg { max-width: var(--container-lg); }
.container--xl { max-width: var(--container-xl); }

/* Spacing Utilities - Margin */
.m-0 { margin: 0; }
.m-1 { margin: var(--space-1); }
.m-2 { margin: var(--space-2); }
.m-3 { margin: var(--space-3); }
.m-4 { margin: var(--space-4); }
.m-5 { margin: var(--space-5); }
.m-6 { margin: var(--space-6); }
.m-8 { margin: var(--space-8); }
.m-10 { margin: var(--space-10); }
.m-12 { margin: var(--space-12); }
.m-16 { margin: var(--space-16); }
.m-20 { margin: var(--space-20); }
.m-24 { margin: var(--space-24); }
.m-32 { margin: var(--space-32); }

.mt-0 { margin-top: 0; }
.mt-1 { margin-top: var(--space-1); }
.mt-2 { margin-top: var(--space-2); }
.mt-3 { margin-top: var(--space-3); }
.mt-4 { margin-top: var(--space-4); }
