/**
 * RE Arquitetura - Button Component
 * BEM methodology for button styles
 */

/* Button Base */
.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  font-family: var(--font-sans);
  font-size: var(--text-base);
  font-weight: var(--font-medium);
  letter-spacing: var(--tracking-wide);
  text-transform: uppercase;
  text-decoration: none;
  white-space: nowrap;
  cursor: pointer;
  transition: all var(--transition-normal);
  border: 2px solid transparent;
  padding: var(--space-3) var(--space-6);
  border-radius: var(--radius-md);
  position: relative;
  overflow: hidden;
}

.btn:focus-visible {
  outline: 2px solid var(--color-greige-400);
  outline-offset: 2px;
}

.btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}

/* Button Variants */
.btn--primary {
  background-color: var(--color-black-matte);
  color: var(--color-greige-50);
  border-color: var(--color-black-matte);
}

.btn--primary:hover:not(:disabled) {
  background-color: var(--color-greige-800);
  border-color: var(--color-greige-800);
  transform: translateY(-1px);
}

.btn--primary:active:not(:disabled) {
  transform: translateY(0);
}

.btn--secondary {
  background-color: transparent;
  color: var(--color-text-primary);
  border-color: var(--color-text-primary);
}

.btn--secondary:hover:not(:disabled) {
  background-color: var(--color-text-primary);
  color: var(--color-bg-primary);
  transform: translateY(-1px);
}

.btn--secondary:active:not(:disabled) {
  transform: translateY(0);
}

.btn--outline {
  background-color: transparent;
  color: var(--color-text-primary);
  border-color: var(--color-greige-300);
}

.btn--outline:hover:not(:disabled) {
  border-color: var(--color-text-primary);
  transform: translateY(-1px);
}

.btn--outline:active:not(:disabled) {
  transform: translateY(0);
}

.btn--ghost {
  background-color: transparent;
  color: var(--color-text-primary);
  border-color: transparent;
  padding: var(--space-3) var(--space-4);
}

.btn--ghost:hover:not(:disabled) {
  background-color: var(--color-greige-100);
}

.btn--link {
  background-color: transparent;
  color: var(--color-text-primary);
  border-color: transparent;
  padding: 0;
  border-radius: 0;
  text-transform: none;
  letter-spacing: var(--tracking-normal);
}

.btn--link:hover:not(:disabled) {
  color: var(--color-greige-600);
  text-decoration: underline;
}

/* Button Sizes */
.btn--sm {
  font-size: var(--text-sm);
  padding: var(--space-2) var(--space-4);
}

.btn--lg {
  font-size: var(--text-lg);
  padding: var(--space-4) var(--space-8);
}

.btn--xl {
  font-size: var(--text-xl);
  padding: var(--space-5) var(--space-10);
}

/* Button with Icon */
.btn__icon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 1.25em;
  height: 1.25em;
  margin-left: var(--space-2);
}

.btn__icon--left {
  margin-left: 0;
  margin-right: var(--space-2);
}

/* Button Block */
.btn--block {
  display: flex;
  width: 100%;
}

/* Button Group */
.btn-group {
  display: inline-flex;
  flex-wrap: wrap;
  gap: var(--space-3);
}

.btn-group--vertical {
  flex-direction: column;
}

/* Responsive Button Sizes */
@media (min-width: 768px) {
  .btn--md-mobile {
    font-size: var(--text-base);
    padding: var(--space-3) var(--space-6);
  }
}

@media (max-width: 767px) {
  .btn--md-mobile {
    font-size: var(--text-sm);
    padding: var(--space-2) var(--space-4);
  }
}

/* Loading State */
.btn--loading {
  pointer-events: none;
  position: relative;
  color: transparent !important;
}

.btn--loading::after {
  content: '';
  position: absolute;
  width: 1em;
  height: 1em;
  top: 50%;
  left: 50%;
  margin-left: -0.5em;
  margin-top: -0.5em;
  border: 2px solid currentColor;
  border-right-color: transparent;
  border-radius: 50%;
  animation: btn-spin 0.6s linear infinite;
}

@keyframes btn-spin {
  to {
    transform: rotate(360deg);
  }
}

/* Icon Only Button */
.btn--icon-only {
  padding: var(--space-3);
  aspect-ratio: 1;
}

.btn--icon-only .btn__icon {
  margin: 0;
}

/* Social Buttons */
.btn--social {
  display: inline-flex;
  align-items: center;
  gap: var(--space-3);
  padding: var(--space-3) var(--space-5);
  font-size: var(--text-sm);
  border-color: var(--color-greige-200);
  background-color: var(--color-bg-primary);
  color: var(--color-text-primary);
}

.btn--social:hover:not(:disabled) {
  border-color: var(--color-greige-400);
  background-color: var(--color-greige-100);
}

/* CTA Button */
.btn--cta {
  background-color: var(--color-black-matte);
  color: var(--color-greige-50);
  border-color: var(--color-black-matte);
  font-size: var(--text-lg);
  padding: var(--space-4) var(--space-10);
  letter-spacing: 0.1em;
}

.btn--cta:hover:not(:disabled) {
  background-color: var(--color-greige-800);
  border-color: var(--color-greige-800);
  box-shadow: var(--shadow-lg);
  transform: translateY(-2px);
}

.btn--cta:active:not(:disabled) {
  transform: translateY(0);
}

/**
 * RE Arquitetura & Design - Main Stylesheet
 *
 * @package RE_Arquitetura
 * @version 1.0.0
 */

/* CSS Tokens - Design System */
:root {
	/* Greige Tones */
	--color-greige-50: #faf8f6;
	--color-greige-100: #f5f0eb;
	--color-greige-200: #e8e0d8;
	--color-greige-300: #d4ccc4;
	--color-greige-400: #b8b0a8;
	--color-greige-500: #9c948c;
	--color-greige-600: #807870;
	--color-greige-700: #645c54;
	--color-greige-800: #484038;
	--color-greige-900: #2c241c;

	/* Sand Accent */
	--color-sand-100: #f0e6dc;
	--color-sand-200: #e0d0c0;
	--color-sand-300: #d0bba4;

	/* Matte Black */
	--color-black-matte: #1a1a1a;

	/* Semantic */
	--color-text-primary: var(--color-greige-900);
	--color-text-secondary: var(--color-greige-600);
	--color-bg-primary: var(--color-greige-50);
	--color-bg-secondary: var(--color-greige-100);

	/* Font Families */
	--font-serif: 'Playfair Display', Georgia, serif;
	--font-sans: 'Montserrat', system-ui, sans-serif;

	/* Modular Scale (1.250 - Major Third) */
	--text-xs: 0.64rem;    /* 10.24px */
	--text-sm: 0.8rem;     /* 12.8px */
	--text-base: 1rem;     /* 16px */
	--text-lg: 1.25rem;    /* 20px */
	--text-xl: 1.563rem;   /* 25px */
	--text-2xl: 1.953rem;  /* 31.25px */
	--text-3xl: 2.441rem;  /* 39.06px */
	--text-4xl: 3.052rem;  /* 48.83px */
	--text-5xl: 3.815rem;  /* 61.04px */
	--text-6xl: 4.769rem;  /* 76.3px */

	/* Weights */
	--font-light: 300;
	--font-normal: 400;
	--font-medium: 500;

	/* Spacing (Base 8px) */
	--space-1: 0.25rem;  /* 4px */
	--space-2: 0.5rem;   /* 8px */
	--space-3: 0.75rem;  /* 12px */
	--space-4: 1rem;     /* 16px */
	--space-5: 1.25rem;  /* 20px */
	--space-6: 1.5rem;   /* 24px */
	--space-8: 2rem;     /* 32px */
	--space-10: 2.5rem;  /* 40px */
	--space-12: 3rem;    /* 48px */
	--space-16: 4rem;    /* 64px */
	--space-20: 5rem;    /* 80px */
	--space-24: 6rem;    /* 96px */
	--space-32: 8rem;    /* 128px */
}

/* Container System */
.container {
	width: 100%;
	max-width: 1280px;
	margin-left: auto;
	margin-right: auto;
	padding-left: var(--space-4);
	padding-right: var(--space-4);
}

@media (min-width: 640px) {
	.container {
		padding-left: var(--space-6);
		padding-right: var(--space-6);
	}
}

@media (min-width: 1024px) {
	.container {
		padding-left: var(--space-8);
		padding-right: var(--space-8);
	}
}

/* Site Header */
.site-header {
	padding: var(--space-6) 0;
	background-color: var(--color-bg-primary);
	border-bottom: 1px solid var(--color-greige-200);
}

.site-header .container {
	display: flex;
	align-items: center;
	justify-content: space-between;
}

.site-logo {
	font-family: var(--font-serif);
	font-size: var(--text-xl);
	font-weight: var(--font-normal);
	color: var(--color-text-primary);
}

/* Main Navigation */
.main-navigation {
	display: flex;
	align-items: center;
}

.main-navigation ul {
	display: flex;
	list-style: none;
	margin: 0;
	padding: 0;
	gap: var(--space-8);
}

.main-navigation a {
	font-family: var(--font-sans);
	font-size: var(--text-sm);
	font-weight: var(--font-medium);
	color: var(--color-text-secondary);
	text-transform: uppercase;
	letter-spacing: 0.05em;
}

.menu-toggle {
	display: none;
	flex-direction: column;
	gap: 4px;
	padding: var(--space-2);
	background: none;
	border: none;
	cursor: pointer;
}

.menu-toggle span {
	display: block;
	width: 24px;
	height: 2px;
	background-color: var(--color-text-primary);
	transition: transform 0.3s ease, opacity 0.3s ease;
}

@media (max-width: 767px) {
	.main-navigation {
		position: fixed;
		top: 0;
		right: -100%;
		width: 80%;
		height: 100vh;
		background-color: var(--color-bg-primary);
		transition: right 0.3s ease;
		z-index: 999;
		box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
	}

	.main-navigation.active {
		right: 0;
	}

	.main-navigation ul {
		flex-direction: column;
		padding: var(--space-8);
		gap: var(--space-6);
	}

	.menu-toggle {
		display: flex;
		z-index: 1000;
	}
}

/* Site Main */
.site-main {
	min-height: 60vh;
	padding: var(--space-12) 0;
}

/* Entry Styles */
.entry-header {
	margin-bottom: var(--space-8);
}

.entry-title {
	font-size: var(--text-4xl);
	color: var(--color-text-primary);
	margin-bottom: var(--space-4);
}

