/**
 * RE Arquitetura & Design - Main JavaScript
 *
 * @package RE_Arquitetura
 * @version 1.0.0
 */

(function() {
	'use strict';

	/**
	 * Mobile Menu Toggle
	 */
	const initMobileMenu = function() {
		const menuToggle = document.querySelector('.menu-toggle');
		const mainNav = document.querySelector('.main-navigation');

		if (!menuToggle || !mainNav) {
			return;
		}

		menuToggle.addEventListener('click', function() {
			const isExpanded = menuToggle.getAttribute('aria-expanded') === 'true';

			menuToggle.setAttribute('aria-expanded', !isExpanded);
			mainNav.classList.toggle('active');

			// Prevent body scroll when menu is open
			document.body.style.overflow = !isExpanded ? 'hidden' : '';
		});

		// Close menu when clicking outside
		document.addEventListener('click', function(event) {
			const isClickInsideNav = mainNav.contains(event.target) || menuToggle.contains(event.target);

			if (!isClickInsideNav && mainNav.classList.contains('active')) {
				menuToggle.setAttribute('aria-expanded', 'false');
				mainNav.classList.remove('active');
				document.body.style.overflow = '';
			}
		});

		// Close menu on escape key
		document.addEventListener('keydown', function(event) {
			if (event.key === 'Escape' && mainNav.classList.contains('active')) {
				menuToggle.setAttribute('aria-expanded', 'false');
				mainNav.classList.remove('active');
				document.body.style.overflow = '';
				menuToggle.focus();
			}
		});

		// Close menu on window resize to desktop
		window.addEventListener('resize', function() {
			if (window.innerWidth >= 768 && mainNav.classList.contains('active')) {
				menuToggle.setAttribute('aria-expanded', 'false');
				mainNav.classList.remove('active');
				document.body.style.overflow = '';
			}
		});
	};

	/**
	 * Smooth Scroll for Anchor Links
	 */
	const initSmoothScroll = function() {
		const anchorLinks = document.querySelectorAll('a[href^="#"]');

		anchorLinks.forEach(function(link) {
			link.addEventListener('click', function(event) {
				const href = this.getAttribute('href');

				if (href === '#' || href === '#main-content') {
					return;
				}

				const targetElement = document.querySelector(href);

				if (targetElement) {
					event.preventDefault();
					targetElement.scrollIntoView({
						behavior: 'smooth',
						block: 'start'
					});

					// Update focus for accessibility
					targetElement.setAttribute('tabindex', '-1');
					targetElement.focus();
				}
			});
		});
	};

	/**
	 * Initialize Everything on DOMContentLoaded
	 */
	if (document.readyState === 'loading') {
		document.addEventListener('DOMContentLoaded', function() {
			initMobileMenu();
			initSmoothScroll();
		});
	} else {
		initMobileMenu();
		initSmoothScroll();
	}

})();

