/**
 * Manifesto Component Styles
 *
 * Philosophy section with refined typography and generous spacing
 * Uses CSS custom properties for theming and responsive design
 *
 * @package RE_Arquitetura
 */

/* ==========================================================================
   MANIFESTO SECTION
   ========================================================================== */

.manifesto {
    position: relative;
    padding: var(--space-24, 6rem) var(--space-6, 1.5rem);
    background-color: var(--color-bg-primary, #faf8f6);
}

/* ==========================================================================
   MANIFESTO HEADER
   ========================================================================== */

.manifesto__header {
    text-align: center;
    margin-bottom: var(--space-16, 4rem);
}

.manifesto__title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-4xl, 3.052rem);
    font-weight: var(--font-normal, 400);
    line-height: var(--leading-tight, 1.1);
    color: var(--color-text-primary, #2c2a27);
    margin-bottom: var(--space-6, 1.5rem);
}

@media (min-width: 768px) {
    .manifesto__title {
        font-size: var(--text-5xl, 3.815rem);
    }
}

.manifesto__divider {
    width: 60px;
    height: 1px;
    background-color: var(--color-greige-300, #c5bfb5);
    margin: 0 auto;
}

/* ==========================================================================
   MANIFESTO CONTENT
   ========================================================================== */

.manifesto__content {
    max-width: 720px;
    margin: 0 auto;
}

.manifesto__text {
    font-family: var(--font-sans, system-ui, -apple-system, sans-serif);
    font-size: var(--text-base, 1rem);
    font-weight: var(--font-light, 300);
    line-height: var(--leading-relaxed, 1.75);
    color: var(--color-text-secondary, #5c5852);
    margin-bottom: var(--space-6, 1.5rem);
    text-align: justify;
    text-justify: inter-word;
}

@media (min-width: 768px) {
    .manifesto__text {
        font-size: var(--text-lg, 1.125rem);
        margin-bottom: var(--space-8, 2rem);
    }
}

/* Lead paragraph - centered, larger */
.manifesto__text--lead {
    font-size: var(--text-lg, 1.125rem);
    font-weight: var(--font-normal, 400);
    text-align: center;
    text-justify: none;
}

@media (min-width: 768px) {
    .manifesto__text--lead {
        font-size: var(--text-2xl, 1.563rem);
    }
}

/* Highlight paragraph - centered, serif, italic */
.manifesto__text--highlight {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-lg, 1.125rem);
    font-style: italic;
    text-align: center;
    color: var(--color-text-primary, #2c2a27);
