/**
 * Diferencial Component Styles
 *
 * Technical differentiator section with card grid layout
 * Performance-optimized with smooth hover transitions
 *
 * @package RE_Arquitetura
 */

/* ==========================================================================
   DIFERENCIAL SECTION
   ========================================================================== */

.diferencial {
    position: relative;
    padding: var(--space-24, 6rem) var(--space-6, 1.5rem);
    background-color: var(--color-bg-secondary, #f0ede8);
}

/* ==========================================================================
   DIFERENCIAL HEADER
   ========================================================================== */

.diferencial__header {
    text-align: center;
    margin-bottom: var(--space-20, 5rem);
}

.diferencial__title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-4xl, 3.052rem);
    font-weight: var(--font-normal, 400);
    color: var(--color-text-primary, #2c2a27);
    margin-bottom: var(--space-4, 1rem);
}

@media (min-width: 768px) {
    .diferencial__title {
        font-size: var(--text-5xl, 3.815rem);
    }
}

.diferencial__subtitle {
    font-family: var(--font-sans, system-ui, -apple-system, sans-serif);
    font-size: var(--text-base, 1rem);
    font-weight: var(--font-light, 300);
    letter-spacing: var(--tracking-wide, 0.025em);
    color: var(--color-text-secondary, #5c5852);
    margin-bottom: var(--space-6, 1.5rem);
}

@media (min-width: 768px) {
    .diferencial__subtitle {
        font-size: var(--text-lg, 1.125rem);
    }
}

.diferencial__divider {
    width: 60px;
    height: 1px;
    background-color: var(--color-greige-300, #c5bfb5);
    margin: 0 auto;
}

/* ==========================================================================
   DIFERENCIAL GRID
   ========================================================================== */

.diferencial__grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: var(--space-12, 3rem);
}

@media (min-width: 768px) {
    .diferencial__grid {
        grid-template-columns: repeat(2, 1fr);
        gap: var(--space-16, 4rem);
    }
}

/* ==========================================================================
   DIFERENCIAL CARD
   ========================================================================== */

.diferencial__card {
    display: flex;
    flex-direction: column;
    background-color: var(--color-bg-primary, #faf8f6);
    border-radius: var(--radius-none, 0);
    overflow: hidden;
}

.diferencial__image {
    aspect-ratio: 4/3;
    overflow: hidden;
    position: relative;
}

.diferencial__image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform var(--transition-slow, 600ms cubic-bezier(0.4, 0, 0.2, 1));
}

.diferencial__card:hover .diferencial__image img {
    transform: scale(1.05);
}

.diferencial__content {
    padding: var(--space-8, 2rem);
}

@media (min-width: 768px) {
    .diferencial__content {
        padding: var(--space-10, 2.5rem);
    }
}

.diferencial__card-title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-2xl, 1.563rem);
    font-weight: var(--font-normal, 400);
    color: var(--color-text-primary, #2c2a27);
    margin-bottom: var(--space-4, 1rem);
}

@media (min-width: 768px) {
    .diferencial__card-title {
        font-size: var(--text-3xl, 2.441rem);
    }
}

.diferencial__card-text {
    font-family: var(--font-sans, system-ui, -apple-system, sans-serif);
    font-size: var(--text-base, 1rem);
    font-weight: var(--font-light, 300);
    line-height: var(--leading-relaxed, 1.75);
    color: var(--color-text-secondary, #5c5852);
}

@media (min-width: 768px) {
    .diferencial__card-text {
        font-size: var(--text-lg, 1.125rem);
    }
}

/* ==========================================================================
   ACCESSIBILITY & REDUCED MOTION
   ========================================================================== */

@media (prefers-reduced-motion: reduce) {
    .diferencial__image img {
        transition: none;
    }

    .diferencial__card:hover .diferencial__image img {
        transform: none;
    }
}

/**
 * Portfolio Component Styles
 *
 * Gallery grid with category filters and lazy loading
 * Uses CSS Grid with responsive breakpoints
 *
 * @package RE_Arquitetura
 */

/* ==========================================================================
   PORTFOLIO SECTION
   ========================================================================== */

.portfolio {
    position: relative;
    padding: var(--space-24, 6rem) var(--space-6, 1.5rem);
    background-color: var(--color-bg-primary, var(--color-greige-50));
}

/* ==========================================================================
   PORTFOLIO HEADER
   ========================================================================== */

.portfolio__header {
    text-align: center;
    margin-bottom: var(--space-16, 4rem);
}

.portfolio__title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-4xl, 3.052rem);
    font-weight: var(--font-normal, 400);
    color: var(--color-text-primary, var(--color-greige-900));
    margin-bottom: var(--space-4, 1rem);
}

@media (min-width: 768px) {
    .portfolio__title {
        font-size: var(--text-5xl, 3.815rem);
    }
}

.portfolio__subtitle {
    font-family: var(--font-sans, 'Montserrat', system-ui, sans-serif);
    font-size: var(--text-base, 1rem);
    font-weight: var(--font-light, 300);
    letter-spacing: var(--tracking-wide, 0.05em);
    color: var(--color-text-secondary, var(--color-greige-600));
}

/* ==========================================================================
   PORTFOLIO FILTERS
   ========================================================================== */

.portfolio__filters {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: var(--space-4, 1rem);
    margin-bottom: var(--space-12, 3rem);
}

.portfolio__filter {
    padding: var(--space-3, 0.75rem) var(--space-6, 1.5rem);
    border: 1px solid var(--color-greige-300, #d4ccc4);
    background-color: transparent;
    color: var(--color-text-secondary, var(--color-greige-600));
    font-family: var(--font-sans, 'Montserrat', system-ui, sans-serif);
    font-size: var(--text-sm, 0.8rem);
    font-weight: var(--font-normal, 400);
    letter-spacing: var(--tracking-wide, 0.05em);
    text-transform: uppercase;
    cursor: pointer;
    transition: all var(--transition-normal, 300ms ease-in-out);
}

.portfolio__filter:hover,
.portfolio__filter:focus {
    background-color: var(--color-text-primary, var(--color-greige-900));
    border-color: var(--color-text-primary, var(--color-greige-900));
    color: var(--color-greige-50, #faf8f6);
    outline: none;
}

.portfolio__filter--active {
    background-color: var(--color-text-primary, var(--color-greige-900));
    border-color: var(--color-text-primary, var(--color-greige-900));
    color: var(--color-greige-50, #faf8f6);
}

.portfolio__filter:focus-visible {
    outline: 2px solid var(--color-text-primary, var(--color-greige-900));
    outline-offset: 4px;
}

/* ==========================================================================
   PORTFOLIO GRID
   ========================================================================== */

.portfolio__grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: var(--space-6, 1.5rem);
}

@media (min-width: 640px) {
    .portfolio__grid {
        grid-template-columns: repeat(2, 1fr);
        gap: var(--space-8, 2rem);
    }
}

@media (min-width: 1024px) {
    .portfolio__grid {
        grid-template-columns: repeat(3, 1fr);
        gap: var(--space-10, 2.5rem);
    }
}

@media (min-width: 1536px) {
    .portfolio__grid {
        grid-template-columns: repeat(4, 1fr);
    }
}

/* ==========================================================================
   PORTFOLIO ITEMS
   ========================================================================== */

.portfolio__item {
    position: relative;
}

.portfolio__item--hidden {
    display: none;
}

.portfolio__item--visible {
    animation: fadeIn 0.5s ease-out forwards;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* ==========================================================================
   PORTFOLIO LINK
   ========================================================================== */

.portfolio__link {
    display: block;
    position: relative;
    text-decoration: none;
}

.portfolio__link:hover,
.portfolio__link:focus {
    outline: none;
}

.portfolio__link:focus-visible {
    outline: 2px solid var(--color-text-primary, var(--color-greige-900));
    outline-offset: 4px;
    border-radius: var(--radius-md, 0.25rem);
}

/* ==========================================================================
   PORTFOLIO IMAGE
   ========================================================================== */

.portfolio__image-wrapper {
    position: relative;
    aspect-ratio: 4/3;
    overflow: hidden;
    background-color: var(--color-greige-200, #e8e0d8);
}

.portfolio__image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform var(--transition-slow, 500ms ease-in-out);
}

.portfolio__link:hover .portfolio__image,
.portfolio__link:focus .portfolio__image {
    transform: scale(1.05);
}

/* ==========================================================================
   PORTFOLIO OVERLAY
   ========================================================================== */

.portfolio__overlay {
    position: absolute;
    inset: 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    padding: var(--space-6, 1.5rem);
    background-color: rgba(44, 36, 28, 0.85);
    opacity: 0;
    transition: opacity var(--transition-normal, 300ms ease-in-out);
}

.portfolio__link:hover .portfolio__overlay,
.portfolio__link:focus .portfolio__overlay {
    opacity: 1;
}

.portfolio__project-title {
    font-family: var(--font-serif, 'Playfair Display', Georgia, serif);
    font-size: var(--text-xl, 1.563rem);
    font-weight: var(--font-normal, 400);
    color: var(--color-greige-50, #faf8f6);
    text-align: center;
    margin-bottom: var(--space-2, 0.5rem);
}

@media (min-width: 768px) {
    .portfolio__project-title {
        font-size: var(--text-2xl, 1.953rem);
    }
}

.portfolio__project-location {
    font-family: var(--font-sans, 'Montserrat', system-ui, sans-serif);
    font-size: var(--text-sm, 0.8rem);
    font-weight: var(--font-light, 300);
    letter-spacing: var(--tracking-wide, 0.05em);
    color: var(--color-greige-300, #d4ccc4);
    text-align: center;
}

/* ==========================================================================
   ACCESSIBILITY & REDUCED MOTION
   ========================================================================== */

@media (prefers-reduced-motion: reduce) {
    .portfolio__filter {
        transition: none;
    }

    .portfolio__image {
        transition: none;
    }

    .portfolio__overlay {
        transition: none;
    }

    .portfolio__item--visible {
        animation: none;
    }

    /* Always show overlay for keyboard users who prefer reduced motion */
    .portfolio__overlay {
        opacity: 1;
        background-color: rgba(44, 36, 28, 0.4);
    }
}

/* ==========================================================================
   PRINT STYLES
   ========================================================================== */

@media print {
    .portfolio__filters {
        display: none;
    }

    .portfolio__overlay {
        opacity: 1;
        background-color: transparent;
    }

    .portfolio__project-title,
    .portfolio__project-location {
        color: var(--color-text-primary, var(--color-greige-900));
    }
}

/**
 * Portfolio Filter Functionality
 *
 * Vanilla JavaScript for portfolio category filtering
 * Uses data attributes for efficient DOM manipulation
 *
 * @package RE_Arquitetura
 */

(function() {
    'use strict';

    /**
     * Initialize portfolio filters
     */
    function initPortfolioFilters() {
        const filters = document.querySelectorAll('.portfolio__filter');
        const items = document.querySelectorAll('.portfolio__item');

        if (!filters.length || !items.length) {
            return;
        }

        // Add click event listener to each filter button
        filters.forEach(filter => {
            filter.addEventListener('click', function(e) {
                e.preventDefault();

                const category = this.getAttribute('data-filter');

                // Update active filter state
                filters.forEach(f => f.classList.remove('portfolio__filter--active'));
                this.classList.add('portfolio__filter--active');

                // Filter items based on category
                filterItems(items, category);
            });
        });

        // Initialize: show all items with animation
        items.forEach(item => {
            item.classList.add('portfolio__item--visible');
        });
    }

    /**
     * Filter portfolio items by category
     * @param {NodeList} items - Portfolio item elements
     * @param {string} category - Category to filter by
     */
    function filterItems(items, category) {
        items.forEach(item => {
            const itemCategory = item.getAttribute('data-category');

            if (category === 'all' || itemCategory === category) {
                // Show matching items
                item.classList.remove('portfolio__item--hidden');

                // Trigger reflow to restart animation
                item.classList.remove('portfolio__item--visible');
                void item.offsetWidth; // Force reflow
                item.classList.add('portfolio__item--visible');

            } else {
                // Hide non-matching items
                item.classList.remove('portfolio__item--visible');
                item.classList.add('portfolio__item--hidden');
            }
        });
    }

    /**
     * Initialize when DOM is ready
     */
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', initPortfolioFilters);
    } else {
        initPortfolioFilters();
    }

})();

