/**
 * Enqueue Portfolio Component Styles and Scripts
 */
function re_arquitetura_enqueue_portfolio_assets() {
	$theme_version = wp_get_theme()->get('Version');

	// Enqueue portfolio styles
	wp_enqueue_style(
		're-arquitetura-portfolio',
		get_template_directory_uri() . '/assets/css/components/portfolio.css',
		array(),
		$theme_version
	);

	// Enqueue portfolio script
	wp_enqueue_script(
