/**
 * Defer Non-Critical JavaScript
 *
 * Adds defer attribute to non-blocking scripts for better performance
 * Allows HTML parsing to continue without waiting for script execution
 */
function re_arquitetura_defer_scripts($tag, $handle, $src) {
	$defer_scripts = array(
		're-arquitetura-main',
		're-arquitetura-portfolio',
	);

	if (in_array($handle, $defer_scripts)) {
		return str_replace(' src', ' defer src', $tag);
	}

	return $tag;
}
add_filter('script_loader_tag', 're_arquitetura_defer_scripts', 10, 3);

