/**
 * Enable SVG Upload (Admin Only with Security)
 */
function re_arquitetura_enable_svg_upload($mimes) {
	// Restrict SVG upload to administrators only
	if (!current_user_can('manage_options')) {
		return $mimes;
	}

	$mimes['svg'] = 'image/svg+xml';
	return $mimes;
}
add_filter('upload_mimes', 're_arquitetura_enable_svg_upload');

/**
 * Sanitize SVG Upload
 */
function re_arquitetura_sanitize_svg_upload($file, $filename) {
	$ext = strtolower(pathinfo($filename, PATHINFO_EXTENSION));

	if ($ext === 'svg') {
		if (!current_user_can('manage_options')) {
			return array('error' => 'Apenas administradores podem fazer upload de SVG.');
		}

		// Basic SVG content sanitization
		$file['tmp_name'] = re_arquitetura_sanitize_svg_content($file['tmp_name']);
	}

	return $file;
}
add_filter('wp_handle_upload_prefilter', 're_arquitetura_sanitize_svg_upload', 10, 2);

/**
 * Sanitize SVG Content
 */
function re_arquitetura_sanitize_svg_content($file_path) {
	if (!file_exists($file_path)) {
		return $file_path;
	}

	$svg_content = file_get_contents($file_path);

	// Remove scripts and dangerous events
	$dangerous_patterns = array(
		'/<script\b[^>]*>(.*?)<\/script>/is',
		'/on\w+="[^"]*"/i',
		'/javascript:/i',
		'/<embed/i',
		'/<iframe/i',
	);

	$svg_content = preg_replace($dangerous_patterns, '', $svg_content);

	file_put_contents($file_path, $svg_content);

	return $file_path;
}

